import React from "react";
import Card from "./Card";

export default function CardList({ propsList }) {
    const cardElements = propsList.map((cardProps) => {
        return <Card props={cardProps} />;
    })
    let popularElements = [];
    let topRatedElements = [];
    for (let i = 0; i < propsList.length; i++) {
        if (propsList[i].category == "mostPopular") {
            popularElements.push(<Card props={propsList[i]} />);
        }
        else if (propsList[i].category == "topRated") {
            topRatedElements.push(<Card props={propsList[i]} />);
        }
    }
    return (
        <div className="cardlistcategory">
            <h1 className="cardlisttitle">Popular Films</h1>
            <div className="cardlist">
                {popularElements}
                {topRatedElements}
            </div>
        </div>
    )
}
