import React from "react";
import Titlebar from "./components/Titlebar";
import Search from "./components/Search";
import Footer from "./components/Footer";
import Home from "./pages/Home";
import Movies from "./pages/Movies";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import MovieProfile from "./pages/MovieProfile";
import { Component } from 'react';

class App extends Component {
    state = {
        data: null
    };

    componentDidMount() {
        this.callBackendAPI()
            .then(res => this.setState({ data: res.express }))
            .catch(err => console.log(err));
    }
    // fetching the GET route from the Express server which matches the GET route from server.js
    callBackendAPI = async () => {
        const response = await fetch('https://api.themoviedb.org/3/discover/movie?api_key=881f6eb3c09910233d3dcb3618c372d7&primary_release_year=2017&sort_by=revenue.desc');
        const body = await response.json();
        console.log(this.state.data);

        if (response.status !== 200) {
            throw Error(body.message)
        }
        return body;
    };



    render() {
        return (
            <div className="App">
                <header className="App-header">
                    <h1 className="App-title">Welcome, {this.state.data} to React</h1>
                </header>
                <BrowserRouter>
                    <Routes>
                        <Route path="/" element={<Home />} />
                        <Route path="movies" element={<Movies />} />
                        <Route path="there-will-be-blood" element={<MovieProfile />} />
                    </Routes>
                </BrowserRouter>
            </div>

        );
    }
}

export default App;
