import React from "react";
import twbb from "../images/there-will-be-blood.jpg"
import { useNavigate } from "react-router-dom";

export default function Card({ props }) {
    const navigate = useNavigate();

    const navigateToMovie = () => {
        // üëáÔ∏è navigate to /contacts
        window.location.href = `/check/${props.title}`;
    };


    try {
        const imgsource = "https://image.tmdb.org/t/p/original" + props.posterPath
        return (
            <div className="card" onClick={navigateToMovie}>
                <img src={imgsource} alt="there will be blood" className="poster" />
                <div className="movie--title">{props.title}</div>
                <div className="movie--genre">
                    {props.genre}
                </div>
            </div>
        );
    } catch (e) {
        console.log(e)
    }

}
