import React, { useContext, useEffect } from "react";
import CardList from "../components/CardList";
import SecondList from "../components/SecondList";
import Footer from "../components/Footer";
import ProfileHeader from "../components/ProfileHeader";
import axios from "axios";
import { useState } from "react";

export default function Movies() {
    const api_url = 'http://127.0.0.1:8000/api/movie-list/'
    const [product, setProduct] = useState(null)

    useEffect(() => {
        axios.get(api_url).then(
            response => {
                setProduct(response.data)
            }
        )
    }, [api_url])

    if (product) {
        let popularElements = [];
        let topRatedElements = [];
        for (let i = 0; i < product.length; i++) {
            if (product[i].category == "mostPopular") {
                popularElements.push(product[i]);
            }
            else if (product[i].category == "topRated") {
                topRatedElements.push(product[i]);
            }
        }
        console.log(popularElements)
        return (
            <div className="moviespage">
                <ProfileHeader />
                <CardList propsList={popularElements} />
                <CardList props={topRatedElements} />
                <Footer />
            </div>

        )
    }
    else {
        return (
            <div>
                <h1>Loading...</h1>
            </div>
        )
    }
}
