import React from "react";
import Header from "../components/Header";
import Titlebar from "../components/Titlebar";
import bladeRunner from "../images/blade-runner3.png"
import MoviesBoard from "../components/MoviesBoard";
import Search from "../components/Search";
import "../style.css"
import axios from "axios";

export default function Movies() {
    const api_url = 'http://127.0.0.1:8000/api/movie-list/'
    const [search, setSearch] = React.useState(0);
    const [product, setProduct] = useState(null)

    useEffect(() => {
        axios.get(api_url).then(
            response => {
                setProduct(response.data)
            }
        )
    }, [api_url])

    if (product) {
        return (
            <div className="moviespage">
                <Header search={search} setSearch={setSearch} />
                <Titlebar image={bladeRunner} firstline={"Popular Films"} movieTitle={"Blade Runner"}
                    movieYear={1982} top={"70px"} height={"40px"} />
                < Search state={search} setState={setSearch} />
                <MoviesBoard />
            </div>
        )
    }
}
