import React from "react";
import twbb from "../images/there-will-be-blood.jpg"
import { useNavigate } from "react-router-dom";

export default function Card(props) {
    const navigate = useNavigate();

    const navigateToMovie = () => {
        // üëáÔ∏è navigate to /contacts
        navigate('/there-will-be-blood');
    };

    console.log(props)
    return (
        <div className="card" onClick={navigateToMovie}>
            <img src={props.posterPath} alt="there will be blood" className="poster" />
            <div className="movie--title">{props.props.title}</div>
            <div className="movie--director">
                {props.props.genre}
            </div>
        </div>
    );
}
