import React from "react";

export default function Hero() {
    return (
        <div className="hero">
            This site uses React and the Movie Database API to recommend movies based on your input.
            There is also sentiment analysis on the movie reviews to help you decide which movies to watch.
            Based on the score of the movie, you can also see the average score of the movie.
            Please enter a movie title in the search bar.
        </div>
    );
}
