import React, { useContext, useEffect } from "react";
import "../stylesheets/Search.css";
import closeIcon from "../images/removed-bg.png";
import SearchCard from "./SearchCard";
import annette from "../images/annette.jpg";
import { useState } from "react";
import axios from "axios";

export default function Sidebar({ state, setState }) {
    const api_url = 'http://127.0.0.1:8000/api/movie-detail/'
    const [product, setProduct] = useState(null)

    useEffect(() => {
        axios.get(api_url).then(
            response => {
                setProduct(response.data)
            }
        )
    }, [api_url])



    changeWidth(state);
    return (
        <div className="sidebar" id="sidebar">
            <div className="sidebar-container">
                <img src={closeIcon} alt="closeIcon" className="closeIcon" onClick={() => setState(0)} />
                <div className="search">SEARCH</div>
                <form name="searchbox" target="_self" method="GET">
                    <input type="text" className="searchInput" id="searchInput" name="searchInput" placeholder="SEARCH" />
                    <input type="submit" className="searchSubmit" value="SEARCH" hidden />
                </form>
                <div className="search-results">
                    <SearchCard posterPath={annette} title={"Annette"} releaseDate={"7 July, 2021"} director={"Leos Carax"} />
                    {/* <SearchCard />
                    <SearchCard />
                    <SearchCard /> */}
                </div>
            </div>
        </div>
    );

}

function changeWidth(state) {
    const a = document.getElementById("sidebar");
    if (a) {
        if (state == 0) {
            a.style.width = "0px";
        }
        else {
            a.style.width = "600px";
        }
    }
}
