import React from "react";
import twbb from "../images/there-will-be-blood.jpg"
import { useNavigate } from "react-router-dom";

export default function Card() {
    const navigate = useNavigate();

    const navigateToMovie = () => {
        // üëáÔ∏è navigate to /contacts
        navigate('/there-will-be-blood');
    };

    return (
        <div className="card" onClick={navigateToMovie}>
            <img src={twbb} alt="there will be blood" className="poster" />
            <div className="movie--title">THERE WILL BE BLOOD</div>
            <div className="movie--director">
                Paul Thomas Anderson
            </div>
        </div>
    );
}
