import React, { useContext, useEffect } from "react";
import Footer from "../components/Footer";
import ProfileHeader from "../components/ProfileHeader";
import ProfileMainContent from "../components/ProfileMainContent";
import Reviews from "../components/Reviews";
import axios from "axios";
import { useState } from "react";

export default function MovieProfile() {
    const api_url = 'http://127.0.0.1:8000/api/movie-detail/'
    const [product, setProduct] = useState(null)

    useEffect(() => {
        axios.get(api_url).then(
            response => {
                setProduct(response.data)
            }
        )
    }, [api_url])

    if (product) {
        product = product[0]
        let crew = product.crew
        let director
        for (let member of crew) {
            if (member.job == 'Director') {
                director = member.name
                break
            }
        }



        return (
            <div className="movieprofile">
                <ProfileHeader />
                <ProfileMainContent director={director} title={product['title']} synopsis={product.synopsis} posterPath={product.posterPath} />
                <Reviews />
                <Footer />
            </div>
        )
    }
    else {
        return (
            <div>
                <h1>Loading...</h1>
            </div>
        )
    }
}
