import React from "react";
import { useRouter } from 'next/router'
import { useState } from 'react'

export default function Search() {
    const router = useRouter()
    const [route, setRoute] = useState()
    const handleSubmit = (e) => {
        e.preventDefault()
        router.push("/check/" + route)
    }

    const navigateToProfile = () => {
        window.location.href = "/check";
    }
    return (
        <div className="search">
            <form name="searchBox" target="_blank" method="GET">
                <input type="text" placeholder="Search for a movie" className="searchbar" id="searchbar" name="searchbar"
                    onChange={(e) => { setRoute(e.target.value) }} />
                <input onSubmit={handleSubmit} type="submit" value="submit" hidden />
            </form>
        </div>
    );
}
